export const cookedGroups = (() => {
  let cookingGroups = groups
  cookingGroups.forEach((group: ExpeditionGroup) => {
    group.expeditions.forEach((expedition: Expedition) => {
      expedition.groupId = group.id

      expedition.nodes.forEach((node: ExpeditionNode) => {
        node.groupId = group.id
        node.expeditionId = expedition.id
      })

      let firstNode = expedition.nodes[0]
      expedition.labels.push({
        text: "Start",
        lat: firstNode.lat,
        lng: firstNode.lng
      })

      let lastNode = expedition.nodes[expedition.nodes.length - 1]
      expedition.labels.push({
        text: "Ende",
        lat: lastNode.lat,
        lng: lastNode.lng
      })

      expedition.labels.forEach((label: ExpeditionLabel) => {
        label.groupId = expedition.groupId
        label.expeditionId = expedition.id
      })
    })
  })
  
  return cookingGroups
})()